// auth.js

// import axios from 'axios';
import axios from "@/utils/axiosHttp";

const state = {
  user: null,
  token: null,
  isLoggedIn: false,
};

const getters = {
  getUser: state => state.user,
  getToken: state => state.token,
  isLoggedIn: state => state.isLoggedIn,
};

const actions = {
  async loginAsync({ commit }, payload) {

    
    try {
      const response = await axios.post('/auth/login', payload)
              .then((res) => {
                  return res.data;
              });
      if (response) {


        localStorage.setItem("HCA_TOKEN", response?.data.authorization?.token);
        localStorage.setItem("HCA_LOGGER", btoa(JSON.stringify(response?.data?.user)));
        localStorage.setItem("HCA_ROLE", response?.data.user.role);
        
        console.log(response?.data)
      }
      return response;
      // const { user, token } = response.data;

    } catch (error) {
      // throw new Error(error.response || 'Login failed');
    }
    
  },
  async login({ commit }, { username, password }) {
    try {
      // const response = await axios.post('/auth/login', {
      //   username,
      //   password,
      // });
      // const { user, token } = response.data;
      commit('setUser', username);
      commit('setToken', password);
      commit('setLoggedIn', true);
    } catch (error) {
      throw new Error(error.response.data.message || 'Login failed');
    }
  },
  logout({ commit }) {
    commit('setUser', null);
    commit('setToken', null);
    commit('setLoggedIn', false);
  },
};

const mutations = {
  setUser(state, user) {
    state.user = user;
  },
  setToken(state, token) {
    state.token = token;
  },
  setLoggedIn(state, status) {
    state.isLoggedIn = status;
  },
};

export default {
  state,
  getters,
  actions,
  mutations,
};
